import React from 'react'
import { Link,NavLink } from 'react-router-dom';
import insta from '../Assets/insta.png';
import whatsapp from '../Assets/whatsapp.png';
import linkedin from '../Assets/linkedin.png';
import { Nav, Navbar, Container, Button } from 'react-bootstrap';
import { useState, useEffect } from 'react';
import '../ComponentsCSS/HeaderStyle.css';



export const Header = () => {
   const[color,setColor]=useState(false);
   const changeColor = ()=>{
    if(window.scrollY >= 100){
        setColor(true);
    }
    else{
        setColor(false);
    }
   }
   window.addEventListener("scroll",changeColor);
    

    return (
        <>
            <Navbar expand="lg" className={color ?"header header-bg" : "header" }>
                <Container>
                    <Navbar.Toggle aria-controls="basic-navbar-nav">
                        <span className='navbar-toggler-icon'></span>
                    </Navbar.Toggle>
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="me-auto">
                            <Nav.Link as={NavLink} to='/' exact ><b>Home</b></Nav.Link>
                            <Nav.Link as={NavLink} to='/skills' exact ><b>Skills</b></Nav.Link>
                            <Nav.Link as={NavLink} to='/projects' exact ><b>Projects</b></Nav.Link>
                        </Nav>
                         </Navbar.Collapse>
                        <span className='navbar-text'>
                            <div className='social-icon'>
                                <Link to='/github'><img variant="link" id='' src={insta} alt="instagram icon" /></Link>&nbsp;&nbsp;&nbsp;&nbsp;
                                <Link to='https://www.linkedin.com/in/jyoti-8b7464242/'><img variant="link" id='linkedin' src={linkedin} alt="linkedin icon" /></Link>&nbsp;&nbsp;&nbsp;&nbsp;
                                <Link to='/whatsapp'><img variant="link" id='whatsapp' src={whatsapp} alt="whatsapp icon" /></Link>&nbsp;&nbsp;&nbsp;&nbsp;
                            </div>
                        </span>
                        <Link to="/contact">
                        <Button variant="outline-light" size="lg"><span>Let's Connect</span></Button>{' '}
                        </Link>
                   
                </Container>
            </Navbar>
        </>
    )
}
