import React from 'react'
import {Link} from 'react-router-dom';
import insta from './assets/insta.png';
import whatsapp from './assets/whatsapp.png';
import linkedin from './assets/in.png';
import {Nav, Navbar,Container,Button} from 'react-bootstrap';


export default function Header() {
    const [activeLink,setActiveLink]=useState('Home');
  const [scrolled,setScrolled]=useState(false);

useEffect (()=>{
  const onScroll=()=>{
    if(window.scrollY > 50){
      setScrolled(true);
    }
    else{
      setScrolled(false);
    }
  }
  window.addEventListener("scroll",onScroll);
},[])  
const onUpdateActionLink=(value)=>{
  setActiveLink(value);
}

    return (
        <>
            <Navbar expand="lg" className={scrolled ? "scrolled" : ""} >
                <Container>
                    <Navbar.Toggle aria-controls="basic-navbar-nav">
                        <span className='navbar-toggler-icon'></span>
                    </Navbar.Toggle>
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="me-auto">
                            <Nav.Link to="#home" className={activeLink === 'home'? 'active navbar-link': 'navbar-link'} onClick={()=>onUpdateActiveLink}><b>Home</b></Nav.Link>
                            <Nav.Link to="#skills"><b>Skills</b></Nav.Link>
                            <Nav.Link to="#projects"><b>Projects</b></Nav.Link>
                        </Nav>
                        <span className='navbar-text'>
                            <div className='social-icon'>
                                <Link to='#'><img id='insta' src={insta} alt="instagram icon" /></Link>&nbsp;&nbsp;&nbsp;&nbsp;
                                <Link to='#'><img id='linkedin' src={linkedin} alt="linkedin icon" /></Link>&nbsp;&nbsp;&nbsp;&nbsp;
                                <Link to='#'><img id='whatsapp' src={whatsapp} alt="whatsapp icon" /></Link>&nbsp;&nbsp;&nbsp;&nbsp;
                            </div>
                        </span>
                        <Button variant="outline-dark" size="lg"><span>Let's Connect</span></Button>{' '}

                    </Navbar.Collapse>
                </Container>
            </Navbar>
        </>
    )
}
