import React from 'react'
import { Link,NavLink } from 'react-router-dom';
import github from '../Assets/github.png';
import hackerrank from '../Assets/hackerrank.png';
import linkedin from '../Assets/linkedin.png';
import { Nav, Navbar, Container, Button } from 'react-bootstrap';
import { useState, useEffect } from 'react';
import '../ComponentsCSS/HeaderStyle.css';




export const Header = () => {
   const[color,setColor]=useState(false);
   const changeColor = ()=>{
    if(window.scrollY >= 100){
        setColor(true);
    }
    else{
        setColor(false);
    }
   }
   window.addEventListener("scroll",changeColor);
    

    return (
        
            <div className='header'>
            <ul>
                <li>
                <Link to='/' exact ><b>Home</b></Link>
                </li>
                <li>
                <Link to='/skills' exact ><b>Skills</b></Link>
                </li>
                <li>
                <Link to='/projects' exact ><b>Projects</b></Link>
                </li>
                <li>
                <Link to='https://www.linkedin.com/in/jyoti-8b7464242/'><img variant="link" id='linkedin' src={linkedin} alt="linkedin icon" /></Link>&nbsp;&nbsp;&nbsp;&nbsp;
                </li>
                <li>
                <Link to='https://github.com/Jyoti0902'><img variant="link" id='github' src={github} alt="github icon" /></Link>&nbsp;&nbsp;&nbsp;&nbsp;
                </li>
                <li>
                <Link to='https://www.hackerrank.com/jyotibrar0902?hr_r=1'><img variant="link" id='hackerrank' src={hackerrank} alt="hackerrank icon" /></Link>&nbsp;&nbsp;&nbsp;&nbsp;
                </li>
            </ul>
            </div>

            
           
          
           



          
    )
}
