import React from 'react'
import { Container, Button } from 'react-bootstrap'
import { useState, useEffect } from 'react';
import { ArrowRight } from 'react-bootstrap-icons';
import {Link} from 'react-router-dom';
import './FrontpgStyles.css';
import headerimg from ".assets/footer-bg.png";

export const Frontpage = () => {
    const myStyle = {
        backgroundImage: `url(${headerimg})`,
        height: '100vh',
        backgroundSize: 'cover',
        backgroundRepeat: 'no-repeat',
    
      };
  return (
    <div style={myStyle}>
            <section className='home text-light' id='home'>
                <Container>
                    <span className='tag-line'><h1 style={{ fontSize: 60 }}>Welcome to my Portfolio</h1></span>
                    <h1 style={{ fontFamily: 'cursive' }}>{`Hi I'm a `}<span className='wrap'>Web developer...</span></h1>
                    <p>&nbsp;I'm Jyoti a Web Developer specialising in React.JS .
                        Additionally,&nbsp;I have <br />&nbsp;experience in HTML & CSS.&nbsp;I create Websites based on your needs.</p>
                    <Link to="/contact">
                        <Button  onClick={() => console.log('connect')}>Let's connect <ArrowRight size={20} /></Button>
                    </Link>
                </Container>
            </section>
        </div>
  )
}
