import React from 'react'
import { Link,NavLink } from 'react-router-dom';
import insta from '../assets/insta.png';
import whatsapp from '../assets/whatsapp.png';
import linkedin from '../assets/linkedin.png';
import { Nav, Navbar, Container, Button } from 'react-bootstrap';
import { useState, useEffect } from 'react';
import { Linking } from 'react';
import 'Hea';



export const Header = () => {
    const [activeLink, setActiveLink] = useState('Home');
    const [scrolled, setScrolled] = useState(false);

    useEffect(() => {
        const onScroll = () => {
            if (window.scrollY > 50) {
                setScrolled(true);
            }
            else {
                setScrolled(false);
            }
        }
        window.addEventListener("scroll", onScroll);
    }, [])
    const onUpdateActionLink = (value) => {
        setActiveLink(value);
    }
    const URL = 'linkedin.com/in/jyoti-8b7464242';
const App = () => {
  useEffect(() => {
    Linking.openURL(URL).catch(err => console.error('An error occurred', err));
  });
    
}
    return (
        <>
            <Navbar expand="lg" className={scrolled ? "scrolled" : ""} >
                <Container>
                    <Navbar.Toggle aria-controls="basic-navbar-nav">
                        <span className='navbar-toggler-icon'></span>
                    </Navbar.Toggle>
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="me-auto">
                            <Nav.Link as={NavLink} to='/' exact className={activeLink === 'home' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActionLink('home')}><b>Home</b></Nav.Link>
                            <Nav.Link as={NavLink} to='/skills' exact className={activeLink === 'skills' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActionLink('skills')}><b>Skills</b></Nav.Link>
                            <Nav.Link as={NavLink} to='/projects' exact className={activeLink === 'projects' ? 'active navbar-link' : 'navbar-link'} onClick={() => onUpdateActionLink('projects')}><b>Projects</b></Nav.Link>
                        </Nav>
                        <span className='navbar-text'>
                            <div className='social-icon'>
                                <Link to='/instagram'><img variant="link" id='insta' src={insta} alt="instagram icon" /></Link>&nbsp;&nbsp;&nbsp;&nbsp;
                                <Link to='/linkedin'><img variant="link" id='linkedin' src={linkedin} alt="linkedin icon" /></Link>&nbsp;&nbsp;&nbsp;&nbsp;
                                <Link to='/whatsapp'><img variant="link" id='whatsapp' src={whatsapp} alt="whatsapp icon" /></Link>&nbsp;&nbsp;&nbsp;&nbsp;
                            </div>
                        </span>
                        <Link to="/contact">
                        <Button variant="outline-light" size="lg"><span>Let's Connect</span></Button>{' '}
                        </Link>
                    </Navbar.Collapse>
                </Container>
            </Navbar>
        </>
    )
}
